import React from "react";

import { GoogleLogin } from "@react-oauth/google";
import jwtDecode from "jwt-decode";
import axios from "axios";
import { toast } from "react-toastify";

function GoogleLoginAuth() {
  const isUserAvailable = async (ueerCredentials) => {
    console.log(ueerCredentials);
    const res = await axios.get("http://localhost:5050/users/");
    const data = res.data;
    console.log(data);
    const user = data.find((user) => user.email === ueerCredentials);
    if (user) {
      toast.error(
        "User already exists with this email address please login to continue"
      );
    } else {
      console.log("Login Failed");
    }
    // const user = data.find(() => user.email === ueerCredentials);
    // if (user) {
    //   toast.error("Account already exists with this email address");
    //   console.log("User already exists");
    // } else {
    //   console.log("User does not exist");
    // }
  };

  return (
    <>
      <div className="grid grid-cols-1 gap-4 pt-4">
        <GoogleLogin
          onSuccess={(credentialResponse) => {
            console.log(credentialResponse.credential);
            const USER_CREDENTIAL = jwtDecode(credentialResponse.credential);
            isUserAvailable(USER_CREDENTIAL.email);
          }}
          onError={() => {
            console.log("Login Failed");
          }}
        />
      </div>
    </>
  );
}

export default GoogleLoginAuth;
